import React, { useContext, Component } from 'react';
import { BrowserRouter as Router, Route, Switch, Redirect } from "react-router-dom";
import './App.css';
import SignIn from './Pages/SignIn/SignIn'
import SignUp from './Pages/SignUp/SignUp'
// import ProfilePage from "./Pages/ProfilePage";
// import PasswordReset from "./PasswordReset";
import Main from './Pages/Main/Main'
import { auth } from './firebase';
import { UserContext } from './Providers/UserProvider'


class Application extends Component {

  state = {
    displayNameAdded: false,
    user: null,
  }

  // getUser = () => {
  //   const user = useContext(UserContext);
  //   console.log(user)
  //   return user
  // } 

  componentDidMount() {
    auth.onAuthStateChanged(userAuth => {
      console.log(userAuth)
      this.setState({ user: userAuth })
    })
  }

  updateDisplayNameAdded = () => {
    this.setState({
      displayNameAdded: true
    })
  }

  render() {
    return (
      <div className="Application">
        <Router>
          <Switch>
            <Route exact path="/">
              {this.state.user && this.state.displayNameAdded ? <Redirect to="/main" /> : <SignIn />}
            </Route>
            <Route exact path="/signUp">
              {this.state.user && this.state.displayNameAdded ? <Redirect to="/main" /> : <SignUp updateDisplayNameAdded={this.updateDisplayNameAdded}/>}
            </Route>
            <Route exact path="/main">
              {this.state.user && this.state.displayNameAdded ? <Main />: <Redirect to="/" /> }
            </Route>
            {/* <PasswordReset path="passwordReset" /> */}
          </Switch>
        </Router>
      </div>
    );
  }
}

export default Application;
