import React, { Component } from 'react';
import { BrowserRouter as Router, Route, Switch, Redirect } from "react-router-dom";
import './App.css';
import SignIn from './Pages/SignIn/SignIn'
import SignUp from './Pages/SignUp/SignUp'
import Main from './Pages/Main/Main'
import { auth } from './firebase';



class Application extends Component {

  state = {
    user: null,
    displayNameFromForm: null,
  }

  componentDidMount() {
    auth.onAuthStateChanged(userAuth => {
      console.log(userAuth)
      this.setState({ user: userAuth })
    })
  }

  updateDisplayNameFromForm = (displayName) => {
    console.log(displayName)
    this.setState({
      displayNameFromForm: displayName,
    })
  }

  render() {
    return (
      <div className="Application">
        <Router>
          <Switch>
            <Route exact path="/">
              {this.state.user ?
                <Redirect to="/main" />
                :
                <SignIn />}
            </Route>
            <Route exact path="/signUp"
              render={props => (
                this.state.user ?
                  <Redirect to={{
                    pathname: "/main",
                    state: { displayName: this.state.displayNameAdded }
                  }}
                  />
                  :
                  <SignUp updateDisplayNameFromForm={this.updateDisplayNameFromForm} />
              )}
            />

            <Route exact path="/signUp">
              {this.state.user ?
                <Redirect to={{
                  pathname: "/main",
                  state: { displayName: this.state.displayNameAdded }
                }}
                />
                :
                <SignUp />}
            </Route>
            <Route exact path="/main">
              {!this.state.user ?
                <Redirect to="/" />
                :
                <Main />}
            </Route>
            {/* <PasswordReset path="passwordReset" /> */}
          </Switch>
        </Router>
      </div>
    );
  }
}

export default Application;
